import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import "./Expenses.css";
import ExpensesFilter from "./ExpensesFilter";

const Expenses = (props) => {

  const [expensesFilterVal,setexpensesFilterVal] = useState('');

  const expensesFilter = (expenseYearFilterData) => {
    console.log("!!!!!!Year and Year!!!!!! : " + expenseYearFilterData);
    return setexpensesFilterVal(expenseYearFilterData);
  }

  return (
    <Card className="expenses">
      <ExpensesFilter onExpensesFilter={expensesFilter}/>
      <ExpenseItem
        title={props.items[0].title}
        amout={props.items[0].amount}
        date={props.items[0].date}
      />
      <ExpenseItem
        title={props.items[1].title}
        amout={props.items[1].amount}
        date={props.items[1].date}
      />
      <ExpenseItem
        title={props.items[2].title}
        amout={props.items[2].amount}
        date={props.items[2].date}
      />
    </Card>
  );
};

export default Expenses;
