import { useState } from "react";
import ExpenseForm from "./ExpenseForm";
import "./NewExpense.css";

const NewExpense = (props) => {
  const [newExpenseButton, setNewExpenseButton] = useState(false);

  const saveExpenseDataHandler = (enteredExpenseData) => {
    const expenseData = {
      ...enteredExpenseData,
      id: Math.random().toString(),
    };
    props.onAddExpense(expenseData);
    console.log(expenseData);
  };

  const newExpenseHandler = () => {
    setNewExpenseButton(true);
  };

  let newExpenseContent = <button onClick={newExpenseHandler}>Add New Expense</button>;

  if(newExpenseButton === true){
    newExpenseContent = <ExpenseForm onSaveExpenseDate={saveExpenseDataHandler} />;
  }

  return (
    <div className="new-expense">
      {newExpenseContent}
    </div>
  );
};

export default NewExpense;
