import React, { useState } from "react";
import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import "./Expenses.css";
import ExpenseFilter from "./ExpenseFilter";

const Expenses = (props) => {
  const [expenseFilterVal, setexpenseFilterVal] = useState("2022");

  const expenseFilter = (expenseYearFilterData) => {
    return setexpenseFilterVal(expenseYearFilterData);
  };

  return (
    <Card className="expenses">
      <ExpenseFilter onExpenseFilter={expenseFilter} />
      <ExpenseItem
        title={props.items[0].title}
        amout={props.items[0].amount}
        date={props.items[0].date}
      />
      <ExpenseItem
        title={props.items[1].title}
        amout={props.items[1].amount}
        date={props.items[1].date}
      />
      <ExpenseItem
        title={props.items[2].title}
        amout={props.items[2].amount}
        date={props.items[2].date}
      />
    </Card>
  );
};

export default Expenses;
