import React, { useRef, useState, useReducer } from "react";
import "./ExpenseForm.css";
import ErrorModal from "../UI/ErrorModal";
import Input from "../UI/Input";

const titleReducer = (state, action) => {
  if (action.type === "USER_INPUT") {
    return { value: action.val, isValid: action.val != "" };
  } else if (action.type === "INPUT_BLUR") {
    return { value: state.value, isValid: state.value != "" };
  }
  return { value: "", isValid: false };
};

const amoutReducer = (state, action) => {
  if (action.type === "USER_INPUT") {
    return { value: action.val, isValid: action.val != "" && action.val > 0 };
  } else if (action.type === "INPUT_BLUR") {
    return { value: state.value, isValid: state.value != "" && state.value > 0 };
  }
  return { value: "", isValid: false };
};

const dateReducer = (state, action) => {
  if (action.type === "USER_INPUT") {
    return { value: action.val, isValid: action.val != "" && action.val != "" };
  } else if (action.type === "INPUT_BLUR") {
    return { value: state.value, isValid: state.value != "" && state.value != "" };
  }
  return { value: "", isValid: false };
};

const ExpenseForm = (props) => {
  const titleInputRef = useRef();
  const amountInputRef = useRef();
  const dateInputRef = useRef();

  const [error, setError] = useState();

  const [formIsValid, setFormIsValid] = useState(false);

  const [titleState, dispatchTitle] = useReducer(titleReducer, {
    value: "",
    isValid: null,
  });

  const [amoutState, dispatchAmount] = useReducer(amoutReducer, {
    value: "",
    isValid: null,
  });

  const [dateState, dispatchDate] = useReducer(dateReducer, {
    value: "",
    isValid: null,
  });

  const submitHandler = (event) => {
    event.preventDefault(); // Prevent the default functionality (Automatically reload page)

    // Validation Check
    if (enteredTitle === "") {
      setError({
        title: "Invalid input",
        message: "Plesas enter a valid title (non-empty values).",
      });
      return;
    }

    if (enteredAmount === "") {
      setError({
        title: "Invalid input",
        message: "Plesas enter a valid amout (non-empty values).",
      });
      return;
    }

    if (enteredDate === "") {
      setError({
        title: "Invalid input",
        message: "Plesas enter a valid date (non-empty values).",
      });
      return;
    }

    const expenseData = {
      title: enteredTitle,
      amount: +enteredAmount,
      date: new Date(enteredDate),
    };

    props.onSaveExpenseDate(expenseData);

    // setEnteredTitle(""); // submitted form init.
    // setEnteredAmount("");
    // setEnteredDate("");

    titleInputRef.current.value = "";
    amountInputRef.current.value = "";
    dateInputRef.current.value = "";
  };

  const errorHandler = () => {
    setError(null);
  };

  return (
    <React.Fragment>
      {error && (
        <ErrorModal
          title={error.title}
          message={error.message}
          onConfirm={errorHandler}
        />
      )}
      <form onSubmit={submitHandler}>
        <div className="new-expense__controls">
          <Input label="Title" type="text" ref={titleInputRef} />
          <Input
            label="Amount"
            type="number"
            min="0.01"
            step="0.01"
            ref={amountInputRef}
          />
          <Input
            label="Date"
            type="date"
            min="2019-01-01"
            max="2023-12-31"
            ref={dateInputRef}
          />
          <div className="new-expense__actions">
            <button onClick={props.onCancel}>Cancel</button>
            <button type="submit">Add Expense</button>
          </div>
        </div>
      </form>
    </React.Fragment>
  );
};
export default ExpenseForm;
